DROP TABLE IF EXISTS `apostas`;
CREATE TABLE `apostas` (
  `id` integer AUTO_INCREMENT,
  `usuario_id` integer,
  `prova_id` integer,
  `data_envio` text,
  `pilotos` text,
  `fichas` text,
  `piloto_11` text,
  `nome_prova` text,
  `automatica` integer,
  `temporada` text,
  PRIMARY KEY (`id`)
);

CREATE INDEX idx_apostas_data ON `apostas` (data_envio);
CREATE INDEX idx_apostas_prova ON `apostas` (prova_id);
CREATE INDEX idx_apostas_usuario ON `apostas` (usuario_id);
DROP TABLE IF EXISTS `championship_bets`;
CREATE TABLE `championship_bets` (
  `id` integer AUTO_INCREMENT,
  `user_id` integer NOT NULL,
  `user_nome` text NOT NULL,
  `champion` text NOT NULL,
  `vice` text NOT NULL,
  `team` text NOT NULL,
  `season` integer NOT NULL,
  `bet_time` timestamp,
  PRIMARY KEY (`id`)
);


DROP TABLE IF EXISTS `championship_bets_log`;
CREATE TABLE `championship_bets_log` (
  `id` integer AUTO_INCREMENT,
  `user_id` integer NOT NULL,
  `user_nome` text NOT NULL,
  `champion` text NOT NULL,
  `vice` text NOT NULL,
  `team` text NOT NULL,
  `season` integer NOT NULL,
  `bet_time` timestamp,
  PRIMARY KEY (`id`)
);


DROP TABLE IF EXISTS `championship_results`;
CREATE TABLE `championship_results` (
  `id` integer AUTO_INCREMENT,
  `season` integer NOT NULL,
  `champion` text NOT NULL,
  `vice` text NOT NULL,
  `team` text NOT NULL,
  PRIMARY KEY (`id`)
);


DROP TABLE IF EXISTS `hall_da_fama`;
CREATE TABLE `hall_da_fama` (
  `id` integer AUTO_INCREMENT,
  `usuario_id` integer NOT NULL,
  `temporada` text NOT NULL,
  `posicao_final` integer NOT NULL,
  `pontos` real,
  PRIMARY KEY (`id`)
);

DROP TABLE IF EXISTS `log_apostas`;
CREATE TABLE `log_apostas` (
  `id` integer AUTO_INCREMENT,
  `usuario_id` integer,
  `prova_id` integer,
  `apostador` text,
  `aposta` text,
  `nome_prova` text,
  `pilotos` text,
  `piloto_11` text,
  `tipo_aposta` integer,
  `automatica` integer,
  `data` text,
  `horario` timestamp,
  `temporada` text,
  `status` text,
  `data_criacao` timestamp,
  PRIMARY KEY (`id`)
);

DROP TABLE IF EXISTS `login_attempts`;
CREATE TABLE `login_attempts` (
  `id` integer AUTO_INCREMENT,
  `email` text NOT NULL,
  `tentativa_em` timestamp,
  `sucesso` boolean,
  `ip_address` text,
  PRIMARY KEY (`id`)
);

DROP TABLE IF EXISTS `pilotos`;
CREATE TABLE `pilotos` (
  `id` integer AUTO_INCREMENT,
  `nome` text,
  `equipe` text,
  `status` text,
  `numero` integer,
  PRIMARY KEY (`id`)
);

DROP TABLE IF EXISTS `posicoes_participantes`;
CREATE TABLE `posicoes_participantes` (
  `id` integer AUTO_INCREMENT,
  `prova_id` integer NOT NULL,
  `usuario_id` integer NOT NULL,
  `posicao` integer NOT NULL,
  `pontos` real NOT NULL,
  `data_registro` text,
  `temporada` text,
  PRIMARY KEY (`id`)
);


DROP TABLE IF EXISTS `provas`;
CREATE TABLE `provas` (
  `id` integer AUTO_INCREMENT,
  `nome` text,
  `data` text,
  `horario_prova` text,
  `status` text,
  `tipo` text,
  `temporada` text,
  PRIMARY KEY (`id`)
);

CREATE INDEX idx_provas_status ON `provas` (status);
CREATE INDEX idx_provas_data ON `provas` (data);
DROP TABLE IF EXISTS `resultados`;
CREATE TABLE `resultados` (
  `prova_id` integer,
  `posicoes` text,
  `temporada` text,
  PRIMARY KEY (`prova_id`)
);

CREATE INDEX idx_resultados_prova ON `resultados` (prova_id);
DROP TABLE IF EXISTS `temporadas`;
CREATE TABLE `temporadas` (
  `temporada` text,
  `criado_em` timestamp,
  PRIMARY KEY (`temporada`)
);

DROP TABLE IF EXISTS `usuarios`;
CREATE TABLE `usuarios` (
  `id` integer AUTO_INCREMENT,
  `nome` text,
  `email` text,
  `senha_hash` text,
  `perfil` text,
  `status` text,
  `faltas` integer,
  `criado_em` timestamp,
  PRIMARY KEY (`id`)
);

CREATE INDEX idx_usuarios_status ON `usuarios` (status);
CREATE INDEX idx_usuarios_perfil ON `usuarios` (perfil);
CREATE INDEX idx_usuarios_email ON `usuarios` (email);
